module.exports = {
	
	userHasKey: function(params, session, ewd) {
		params.rpcName = "ORWU NPHASKEY";
		params.rpcArgs = [
			{type: "LITERAL", value: params.duz },
			{type: "LITERAL", value: params.key },
		];
		var response = vistaLib.runRpc(params, session, ewd);
		
		return response.value == "1";
	}
	
	
	
};

var vistaLib = require('VistALib');